import { AppConfig, UserSession } from '@stacks/auth';
import { decodeToken } from 'jsontokens';
import type { AuthOptions, AuthResponsePayload, StacksProvider } from './types';

import { getStacksProvider } from './utils';

export const defaultAuthURL = 'https://app.blockstack.org';

const version = __VERSION__; // replaced via tsup esbuildOptions

if (typeof window !== 'undefined') {
  window.__CONNECT_VERSION__ = version;
}

export const isMobile = () => {
  const ua = navigator.userAgent;
  if (/android/i.test(ua)) {
    return true;
  }
  if (/iPad|iPhone|iPod/.test(ua)) {
    return true;
  }
  return /windows phone/i.test(ua);
};

/**
 * mobile should not use a 'popup' type of window.
 */
export const shouldUsePopup = () => {
  return !isMobile();
};

export const getOrCreateUserSession = (userSession?: UserSession): UserSession => {
  if (!userSession) {
    const appConfig = new AppConfig(['store_write'], document.location.href);
    userSession = new UserSession({ appConfig });
  }
  return userSession;
};

export const authenticate = async (
  authOptions: AuthOptions,
  provider: StacksProvider = getStacksProvider()
) => {
  if (!provider) throw new Error('[Connect] No installed Stacks wallet found');

  const {
    redirectTo = '/',
    manifestPath,
    onFinish,
    onCancel,
    sendToSignIn = false,
    userSession: _userSession,
    appDetails,
  } = authOptions;
  const userSession = getOrCreateUserSession(_userSession);
  if (userSession.isUserSignedIn()) {
    userSession.signUserOut();
  }
  const transitKey = userSession.generateAndStoreTransitKey();
  const authRequest = userSession.makeAuthRequest(
    transitKey,
    `${document.location.origin}${redirectTo}`,
    `${document.location.origin}${manifestPath}`,
    userSession.appConfig.scopes,
    undefined,
    undefined,
    {
      sendToSignIn,
      appDetails,
      connectVersion: version,
    }
  );

  try {
    const authResponse = await provider.authenticationRequest(authRequest);
    await userSession.handlePendingSignIn(authResponse);
    const token = decodeToken(authResponse);
    const payload = token?.payload;
    const authResponsePayload = payload as unknown as AuthResponsePayload;
    onFinish?.({
      authResponse,
      authResponsePayload,
      userSession,
    });
  } catch (error) {
    console.error('[Connect] Error during auth request', error);
    onCancel?.();
  }
};

// eslint-disable-next-line @typescript-eslint/require-await
export const getUserData = async (userSession?: UserSession) => {
  userSession = getOrCreateUserSession(userSession);
  if (userSession.isUserSignedIn()) {
    return userSession.loadUserData();
  }
  if (userSession.isSignInPending()) {
    return userSession.handlePendingSignIn();
  }
  return null;
};
export function getStacksProvider() {
  return window.StacksProvider || window.BlockstackProvider;
}

export function isStacksWalletInstalled() {
  return !!getStacksProvider();
}
import { StacksTestnet } from '@stacks/network';
import { createUnsecuredToken, Json, TokenSigner } from 'jsontokens';

import { getKeys, getUserSession, hasAppPrivateKey } from '../transactions';
import { PsbtPayload, PsbtPopup, PsbtRequestOptions } from '../types/bitcoin';
import { getStacksProvider } from '../utils';
import { StacksProvider } from '../types';

// eslint-disable-next-line @typescript-eslint/require-await
async function signPayload(payload: PsbtPayload, privateKey: string) {
  const tokenSigner = new TokenSigner('ES256k', privateKey);
  // eslint-disable-next-line @typescript-eslint/no-unnecessary-type-assertion
  return tokenSigner.signAsync({ ...payload } as any);
}

export function getDefaultPsbtRequestOptions(options: PsbtRequestOptions) {
  const network = options.network || new StacksTestnet();
  const userSession = getUserSession(options.userSession);
  const defaults: PsbtRequestOptions = {
    ...options,
    network,
    userSession,
  };
  return {
    ...defaults,
  };
}

async function openPsbtPopup({ token, options }: PsbtPopup, provider: StacksProvider) {
  if (!provider) throw new Error('[Connect] No installed Stacks wallet found');

  try {
    const psbtResponse = await provider.psbtRequest(token);
    options.onFinish?.(psbtResponse);
  } catch (error) {
    console.error('[Connect] Error during psbt request', error);
    options.onCancel?.();
  }
}

// eslint-disable-next-line @typescript-eslint/require-await
export const makePsbtToken = async (options: PsbtRequestOptions) => {
  const { allowedSighash, hex, signAtIndex, userSession, ..._options } = options;
  if (hasAppPrivateKey(userSession)) {
    const { privateKey, publicKey } = getKeys(userSession);

    const payload: PsbtPayload = {
      ..._options,
      allowedSighash,
      hex,
      signAtIndex,
      publicKey,
    };

    return signPayload(payload, privateKey);
  }
  const payload = { ..._options };
  return createUnsecuredToken(payload as Json);
};

async function generateTokenAndOpenPopup<T extends PsbtRequestOptions>(
  options: T,
  makeTokenFn: (options: T) => Promise<string>,
  provider: StacksProvider
) {
  const token = await makeTokenFn({
    ...getDefaultPsbtRequestOptions(options),
    ...options,
  } as T);
  return openPsbtPopup({ token, options }, provider);
}

/**
 * @experimental
 */
export function openPsbtRequestPopup(
  options: PsbtRequestOptions,
  provider: StacksProvider = getStacksProvider()
) {
  return generateTokenAndOpenPopup(options, makePsbtToken, provider);
}
import { AppConfig, UserSession } from '@stacks/auth';
import { bytesToHex, hexToBytes } from '@stacks/common';
import { StacksTestnet } from '@stacks/network';
import {
  ChainID,
  deserializeTransaction,
  PostCondition,
  serializeCV,
  serializePostCondition,
} from '@stacks/transactions';
import { createUnsecuredToken, Json, SECP256K1Client, TokenSigner } from 'jsontokens';
import {
  ContractCallOptions,
  ContractCallPayload,
  ContractCallRegularOptions,
  ContractCallSponsoredOptions,
  ContractDeployOptions,
  ContractDeployPayload,
  ContractDeployRegularOptions,
  ContractDeploySponsoredOptions,
  FinishedTxPayload,
  SignTransactionOptions,
  SignTransactionPayload,
  SponsoredFinishedTxPayload,
  STXTransferOptions,
  STXTransferPayload,
  STXTransferRegularOptions,
  STXTransferSponsoredOptions,
  TransactionOptions,
  TransactionPayload,
  TransactionPopup,
  TransactionTypes,
} from '../types/transactions';
import { getStacksProvider } from '../utils';
import { StacksProvider } from '../types';

// TODO extract out of transactions
export const getUserSession = (_userSession?: UserSession) => {
  let userSession = _userSession;

  if (!userSession) {
    const appConfig = new AppConfig(['store_write'], document.location.href);
    userSession = new UserSession({ appConfig });
  }
  return userSession;
};

export function hasAppPrivateKey(userSession?: UserSession) {
  try {
    const session = getUserSession(userSession).loadUserData();
    return session.appPrivateKey;
  } catch (e) {
    return false;
  }
}

export const getKeys = (_userSession?: UserSession) => {
  const userSession = getUserSession(_userSession);
  const privateKey = userSession.loadUserData().appPrivateKey;
  const publicKey = SECP256K1Client.derivePublicKey(privateKey);

  return { privateKey, publicKey };
};

// TODO extract out of transactions
export function getStxAddress(options: TransactionOptions) {
  const { stxAddress, userSession, network } = options;

  if (stxAddress) return stxAddress;
  if (!userSession || !network) return undefined;
  const stxAddresses = userSession?.loadUserData().profile?.stxAddress;
  const chainIdToKey = {
    [ChainID.Mainnet]: 'mainnet',
    [ChainID.Testnet]: 'testnet',
  };
  const address: string | undefined = stxAddresses?.[chainIdToKey[network.chainId]];
  return address;
}

function getDefaults(options: TransactionOptions) {
  const network = options.network || new StacksTestnet();

  const userSession = getUserSession(options.userSession);
  const defaults: TransactionOptions = {
    ...options,
    network,
    userSession,
  };

  return {
    stxAddress: getStxAddress(defaults),
    ...defaults,
  };
}

function encodePostConditions(postConditions: PostCondition[]) {
  return postConditions.map(pc => bytesToHex(serializePostCondition(pc)));
}

// eslint-disable-next-line @typescript-eslint/require-await
async function signPayload(payload: TransactionPayload, privateKey: string) {
  let { postConditions } = payload;
  if (postConditions && typeof postConditions[0] !== 'string') {
    postConditions = encodePostConditions(postConditions as PostCondition[]);
  }
  const tokenSigner = new TokenSigner('ES256k', privateKey);
  // eslint-disable-next-line @typescript-eslint/no-unnecessary-type-assertion
  return tokenSigner.signAsync({ ...payload, postConditions } as any);
}

function createUnsignedTransactionPayload(payload: Partial<TransactionPayload>) {
  let { postConditions } = payload;
  if (postConditions && typeof postConditions[0] !== 'string') {
    postConditions = encodePostConditions(postConditions as PostCondition[]);
  }
  return createUnsecuredToken({ ...payload, postConditions } as unknown as Json);
}

const openTransactionPopup = async (
  { token, options }: TransactionPopup,
  provider: StacksProvider
) => {
  try {
    const txResponse = await provider.transactionRequest(token);
    const { txRaw } = txResponse;
    const txBytes = hexToBytes(txRaw.replace(/^0x/, ''));
    const stacksTransaction = deserializeTransaction(txBytes);

    if ('sponsored' in options && options.sponsored) {
      options.onFinish?.({
        ...(txResponse as SponsoredFinishedTxPayload),
        stacksTransaction,
      });
      return;
    }
    options.onFinish?.({
      ...(txResponse as FinishedTxPayload),
      stacksTransaction,
    });
  } catch (error) {
    console.error('[Connect] Error during transaction request', error);
    options.onCancel?.();
  }
};

// eslint-disable-next-line @typescript-eslint/require-await
export const makeContractCallToken = async (options: ContractCallOptions) => {
  const { functionArgs, appDetails, userSession, ..._options } = options;

  const args: string[] = functionArgs.map(arg => {
    if (typeof arg === 'string') {
      return arg;
    }
    return bytesToHex(serializeCV(arg));
  });
  if (hasAppPrivateKey(userSession)) {
    const { privateKey, publicKey } = getKeys(userSession);
    const payload: ContractCallPayload = {
      ..._options,
      functionArgs: args,
      txType: TransactionTypes.ContractCall,
      publicKey,
    };
    if (appDetails) payload.appDetails = appDetails;
    return signPayload(payload, privateKey);
  }
  const payload: Partial<ContractCallPayload> = {
    ..._options,
    functionArgs: args,
    txType: TransactionTypes.ContractCall,
  };
  if (appDetails) payload.appDetails = appDetails;
  return createUnsignedTransactionPayload(payload);
};

// eslint-disable-next-line @typescript-eslint/require-await
export const makeContractDeployToken = async (options: ContractDeployOptions) => {
  const { appDetails, userSession, ..._options } = options;
  if (hasAppPrivateKey(userSession)) {
    const { privateKey, publicKey } = getKeys(userSession);
    const payload: ContractDeployPayload = {
      ..._options,
      publicKey,
      txType: TransactionTypes.ContractDeploy,
    };
    if (appDetails) payload.appDetails = appDetails;
    return signPayload(payload, privateKey);
  }

  const payload: Partial<ContractDeployPayload> = {
    ..._options,
    txType: TransactionTypes.ContractDeploy,
  };
  if (appDetails) payload.appDetails = appDetails;
  return createUnsignedTransactionPayload(payload);
};

